<template>
    <!-- 歌单详情/搜索内容 -->
   <div class="searchCon">
      <div class="searchTitle" v-for="item in songItem" :key="item.id" >
        <div @click="musicUrl(item.id)" class="rankingLeft">
          <img :src="item.al.picUrl" class="ranking-img" alt="">
          <div class="searchLeft rankingMid">
            <div class="searchN">{{item.name}}</div>
            <div class="searchName">
              <span v-for="(itempeo,indexpeo) in item.ar" :key="indexpeo">
                  {{itempeo.name}}&nbsp;
              </span>
              <span>- {{item.al.name}}</span>
            </div>
          </div>
        </div>
         <i @click="JoinTheList(item.id)" style="background:url(data:image/svg+xml;base64,); background-size:40rpx 40rpx; width:40rpx;height:40rpx;"></i>
      </div>
    </div>
</template>

<script>
var Fly = require("flyio/dist/npm/wx"); //npm引入方式
var fly = new Fly(); //创建fly实例

export default {
  data(){
    return {
      songItem: []
    }
  },
  onLoad(options){
    // 获取到vuex中的url
    let url = this.$store.getters.getStorage;
    // console.log(url);
    fly.get(url,{})
    .then(d=>{
      this.songItem = d.data.playlist.tracks
      // console.log(this.songItem);
      let playCount = d.data.playlist.playCount
      if(playCount >9999){
        playCount = (Math.floor(playCount/1000)/10)+'万'
      }else{
        playCount = playCount
      }
      this.playCount = playCount
      wx.setNavigationBarTitle({
          title: d.data.playlist.name //页面标题为路由参数
      });
    })
    .catch(err=>{
      console.log(err.status,err.message);
    })
  },  
  methods: {
    musicUrl(e) {
      this.$emit('musicUrl',e)
    },
    JoinTheList(e) {
        let chongfu = [];
        let playing = wx.getStorageSync("playing");
        for (let i = 0; i < playing.length; i++) {
            const ele = playing[i].id;
            chongfu.push(ele);
        }
        for (let i= 0; i < chongfu.length; i++) {
            const eleq = chongfu[i];
            if (eleq == e) {
                var panduanchongfu = true;
            }
        }
        if (!panduanchongfu) {
            let status = {
                id: e,
                status: false
            };
            playing.push(status);
            wx.setStorageSync("playing", playing);
        }
    }
  }
}
</script>

<style>
.ranking-img{
  width: 120rpx;
  height: 120rpx;
  min-width: 120rpx;
  min-height: 120rpx;
  border-radius: 10rpx;
}
.rankingLeft{
   display: flex;
}
.rankingMid{
  margin-left: 20rpx;
}
.rankingT{
  border: none !important;
}
</style>